import React from "react";
import {
  View,
  Text,
  StyleSheet,
  TouchableOpacity,
  TouchableNativeFeedback,
  Platform,
  Dimensions,
} from "react-native";

const { width } = Dimensions.get("window");
const diameter = (width - 3 * 10) / 3;

const Clients = (props) => {
  let TouchableCmp = TouchableOpacity;

  if (Platform.OS === "android" && Platform.Version >= 21) {
    TouchableCmp = TouchableNativeFeedback;
  }

  return (
    <TouchableCmp onPress={props.onSelect} useForeground>
      <View style={styles.card}>
        <View style={styles.details}>
          <Text style={styles.text}>{props.clientName}</Text>
          <Text style={styles.text}>{props.phoneNumber}</Text>
          <Text style={styles.text}>{props.companyName}</Text>
          <Text style={styles.text}>{props.address}</Text>
          <Text style={styles.text}>{props.dateJoined}</Text>
        </View>
      </View>
    </TouchableCmp>
  );
};

const styles = StyleSheet.create({
  card: {
    // margin: 20,
    backgroundColor: "white",
    borderRadius: 20,
    padding: 5,
    alignItems: "center",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
    height: 150,
    margin: 5,
    width: width - 10,
  },
  circle: {
    height: diameter,
    margin: 5,
    width: diameter,
    borderRadius: diameter / 2,
    backgroundColor: "green",
  },

  details: {
    alignItems: "center",
    height: "10%",
    padding: 5,
    textAlign: "center",
    marginTop: 10,
  },
  text: {
    fontSize: 12,
    marginVertical: 2,
  },
});

export default Clients;
