import { AUTHENTICATE, LOGOUT, SET_DID_TRY_AL } from '../actions/auth';

const initialState = {
  // email: null,
  token: null,
  userId: null, 
  didTryAutoLogin: false
};

export default (state = initialState, action) => {
  switch (action.type) {
    case AUTHENTICATE:
      return {
        // email: action.email,
        token: action.token,
        userId: action.userId,
        didTryAutoLogin: true
      };
    case SET_DID_TRY_AL:
      return {
        ...state,
        didTryAutoLogin: true
      }
    case LOGOUT:
      return {
        ...initialState,
        didTryAutoLogin: true
      }
    default:
      return state;
  }
};
