import { INPUT_CHANGE, INPUT_BLUR } from "../actions/form";

const initialState = {
  value: "",
  isValid: true,
  touched: false,
};

export default (state = initialState, action) => {
  switch (action.type) {
    case INPUT_CHANGE:
      return {
        ...state,
        value: action.value,
        isValid: action.isValid,
      };
    case INPUT_BLUR:
      return {
        ...state,
        touched: true,
      };
    default:
      return state;
  }
};
